$(document).ready(()=>{let lieu_rencontre=$(".lieu_rencontre .select-wrapper input.select-dropdown");let exempt=$("#exempt");let adversaire=$("#adversaire");let ville_host=$("#ville_host");let reported=$("#reporte");let div_reported=$("#div_reported");let selectsDateRecontre=$("#rencontre_dateRencontre");if(exempt.is(":checked")){div_reported.hide();adversaire.val("").attr("placeholder","Pas d'adversaire").prop("disabled",true);lieu_rencontre.prop("disabled",true);ville_host.prop("disabled",true)}exempt.change(e=>{adversaire.prop("disabled",e.currentTarget.checked);ville_host.prop("disabled",e.currentTarget.checked);lieu_rencontre.prop("disabled",e.currentTarget.checked);if(e.currentTarget.checked){reported.prop("checked",false);div_reported.hide();selectsDateRecontre.hide();adversaire.val("").attr("placeholder","Pas d'adversaire");ville_host.val("")}else{div_reported.show();selectsDateRecontre.show();adversaire.attr("placeholder","Adversaire")}})});