function renouvelerCertifMedical(idCompetiteur,prenom,isFromAdmin){let r=confirm("Renouveler le certificat médical de "+prenom+" ?");if(r){sending(idCompetiteur);$.ajax({url:"/backoffice/competiteur/renouveler/certificat/"+idCompetiteur,type:"POST",dataType:"json",success:function(response){endSending(response,idCompetiteur,isFromAdmin,prenom)},error:function(){endSending({status:false,message:"Une erreur est survenue"},idCompetiteur,isFromAdmin,prenom)}})}}function sending(idCompetiteur){$("#loaderCertif"+idCompetiteur).show();$("#btnCertif"+idCompetiteur).hide()}function endSending(response,idCompetiteur,isFromAdmin,prenom){if(!response.status){M.toast({html:response.message});$("#btnCertif"+idCompetiteur).show()}else{M.toast({html:"Certificat médical de "+prenom+" renouvelé"});if(isFromAdmin)$("#icon-renew-certif"+idCompetiteur).removeClass("red-text").addClass("green-text");else $(".label-renew-certif"+idCompetiteur).removeClass("red").addClass("green");$(".label-renew-certif"+idCompetiteur).html(!isFromAdmin?"Renouvellement pour la rentrée<br><b>"+response.message+"</b>":response.message)}$("#loaderCertif"+idCompetiteur).hide()}