$(window).resize(function(){let tables=document.getElementsByClassName("table-to-adapt-columns");for(const table of tables){if(table.offsetParent!=null){let trs=[...table.rows];if($(window).width()<=983){trs.forEach(element=>element.style.height=Math.max(...trs.map(x=>x.offsetHeight))+"px")}else trs.forEach(element=>element.style.height="auto")}}});$(document).ready(function(){if($(window).width()<=992){let tables=document.getElementsByClassName("table-to-adapt-columns");for(const table of tables){if(table.offsetParent!=null){let trs=[...table.rows];trs.forEach(tr=>tr.style.height=Math.max(...trs.map(x=>x.offsetHeight))+"px")}}}});