function resizeTable(){if($(window).width()<=992){let tables=document.getElementsByClassName("table-to-adapt-columns");for(const table of tables){if(table.offsetParent!=null){let trs=[...table.rows].filter(tr=>tr.id!=="tr-empty-result-search");let newHeight=Math.max(...trs.map(x=>x.offsetHeight));if(newHeight)trs.forEach(tr=>tr.style.height=newHeight+"px")}}}}$(document).ready(()=>{$(".modal").modal();$(".dropdown-trigger").dropdown({coverTrigger:false});$("select").formSelect();$(".sidenav").sidenav({closeOnClick:true,draggable:true,preventScrolling:true});$(".tabs").tabs({onShow:()=>{resizeTable()}});$(".tooltipped").tooltip();$('li[id^="select-options"]').on("touchend",function(e){e.stopPropagation()});$(".collapsible").collapsible({accordion:false});$(".fixed-action-btn").floatingActionButton();Array.from($(".trimInput")).forEach(el=>el.addEventListener("input",()=>{el.value=el.value.trim()}))});