$("#search-input").on("keyup",function(){let value=$(this).val().toUpperCase();$(".search-table tbody tr").each(function(_index){let row=$(this);if(value.length){let nomPrenom=row.find("td:nth-child(3)").text().trim().toUpperCase();let rolesTemp=Array.from(row.find("td:first")[0].children).filter(e=>e.classList.contains("badge")).map(e=>e.dataset.badgeCaption.toUpperCase());if(nomPrenom.indexOf(value)<0&&rolesTemp.filter(role=>role.indexOf(value)>=0).length===0)row.hide();else row.show()}else row.show()})});